import { Router } from '@angular/router';
import { LoginService } from './login.service';
import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  login = {
    username: '',
    senha: ''
  }

  constructor(private BsModalService: ,private loginService: LoginService, private router: Router) { }

  validarLogin(): void {
    this.loginService.validarLogin(this.login.username, this.login.senha).subscribe(() => {
      //this.router.navigate(['/products'])
    })

  }

}
